import collections


class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: int
        """
        values = collections.Counter(s).values()
        odd = -1
        for v in values:
            if v % 2 != 0:
                odd += 1
        if odd < 0:
            return len(s)
        return len(s) - odd

if __name__ == "__main__":
    so = Solution()
